import React, { Component } from 'react';
import $ from 'jquery';
import Search from './components/Search';
class App extends Component {

  state = {
    lookingOnDate : "",
  }

  chooseDate = () =>{
    var date = new Date($('.date').val());
    var formattedDate = date.toLocaleString;

    this.setState({
      lookingOnDate : formattedDate
    })

  }

  render(){
    return (
      <div className="container">
      <div className="header">
        <h3>CHOOSE A DAY AND SEARCH WITH KEYWORDS IN YOUR DAILIES</h3>
      </div>
        <div className="row">
        <div className="col-sm-4 mt-4 dateContainer">
          <input className="date" type="date" onSelect={this.chooseDate}></input>
        </div>
          <Search />
      </div>
      </div>
    );
  }

}

export default App;
