import React, {useState} from 'react'
import AddWorkingPiece from './AddWorkingPiece';
import WorkPiece from './WorkPiece';

const WorkDay = ({id,header,date, handleDeleteWorkingDiary,timePeriodList, passedWorkingPieces}) => {

        const [isVisible,setIsVisible] = useState(false);
        const [workingPieces, setWorkingPieces] = useState (passedWorkingPieces);

        const handleAddWorkingPiece = (belongerId,inheritedDate,header,context,timePeriod) => {

            const belongerWorkingDiary = workingDiaries.filter((workingDiary) => workingDiary.id === belongerId);

            const newWorkingPiece = {
              id: nanoid(),
              header: header,
              context: context,
              timePeriod : timePeriod,
              date: inheritedDate,
            }
            const newWorkingPieces = [...workingPieces, newWorkingPiece];
            setWorkingPieces(newWorkingPieces);
            belongerWorkingDiary.workingPieces = workingPieces;
            setWorkingPieces('');
          }

          const handleDeleteWorkingPiece = (id) => {

            const newWorkingPieces = workingPieces.filter((workingPiece) => id !== workingPiece.id);
            setWorkingPieces(newWorkingPieces);

          }

        return (

             <div className="row mt-4">
                    <div id={id} className="card">

                        <div className="card-header d-flex justify-content-between">
                            <div className="row">
                                <h5 id="header" className="d-inline mr-2" style={{cursor:"pointer"}} onClick={() => setIsVisible(!isVisible)}>{header}</h5>
                                <i onClick={() => handleDeleteWorkingDiary(id)} className="far fa-trash-alt" style = {{cursor:"pointer"}}></i>
                            </div>
                            <div className="row">
                                <label id="workingDiaryDate" >{date}</label>
                                <button id="new-working-piece-button" className="btn btn-primary" data-toggle="modal" data-target="#addWorkingPiece" toggle="modal" type="button" >Yeni Bir İş Parçası Ekle</button>
                            </div>
                       </div>
                        {
                        isVisible ? 
                        <div className="card-body">
                                {workingPieces.map(workingPiece => (
                                    <WorkPiece
                                        id = {workingPiece.id}
                                        header ={workingPiece.header}
                                        context = {workingPiece.context}
                                        date = {workingPiece.date}
                                        timePeriod = {workingPiece.timePeriod}
                                        handleDeleteWorkingPiece = {handleDeleteWorkingPiece}
                                        handleAddWorkingPiece = {handleAddWorkingPiece}
                                    />
                                ))} 

                        </div> : null
                        }
                        <AddWorkingPiece
                            handleAddWorkingPiece = {handleAddWorkingPiece}
                            timePeriodList = {timePeriodList}
                            belongerId = {id}
                            inheritedDate = {date}
                        />
                    </div>
            </div>

        )
    }
export default WorkDay;
