import React, { useState } from 'react';
import $ from 'jquery';
import {nanoid} from 'nanoid';
import Search from './components/Search';
import DiaryList from './components/DiaryList';
import AddWorkingDaily from './components/AddWorkingDaily';






const App = () => {


  const [workingDairies, setWorkingDiaries] = useState([]);
  const [workingPieces, setWorkingPieces] = useState([]);
  const [lookingOnDate, setLookingOnDate] = useState('')
  const [searchText, setSearchText] = useState('');
  const [timePeriodList] = useState(
    {
      0: "00:00 - 01:00",
      1: "01:00 - 02:00",
      2: "02:00 - 03:00",
      3: "03:00 - 04:00",
      4: "04:00 - 05:00",
      5: "05:00 - 06:00",
      6: "06:00 - 07:00",
      7: "07:00 - 08:00",
      8: "08:00 - 09:00",
      9: "09:00 - 10:00",
      10: "10:00 - 11:00",
      11: "11:00 - 12:00",
      12: "12:00 - 13:00",
      13: "13:00 - 14:00",
      14: "14:00 - 15:00",
      15: "15:00 - 16:00",
      16: "16:00 - 17:00",
      17: "17:00 - 18:00",
      18: "18:00 - 19:00",
      19: "19:00 - 20:00",
      20: "20:00 - 21:00",
      21: "21:00 - 22:00",
      22: "22:00 - 23:00",
      23: "23:00 - 00:00"
    }
  );


  // $.getJSON("localhost:8080/getDiaries",function (data){
  //   this.setState({
  //     workingDairies : data
  //   })
  // });

  const fetchDatas = () => {

    fetch("http://localhost:8080/getDiaries",{ mode: 'cors' })
    .then((response) => { 
      var pulledData = response.json();
      console.log(pulledData);
      setWorkingDiaries(pulledData);
      return pulledData})
    console.log($('.date').val());

  }

  const pageOnLoad = () =>{

    //fetchDatas();

  }

  document.getElementsByTagName("body")[0].addEventListener("onload",pageOnLoad);

  const handleAddWorkingDairy = (header,date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    const newWorkingDairy = {
      id: nanoid(),
      header: header,
      date: formattedDate,
      workingPieces: []
    }
    const newWorkingDiaries = [...workingDairies, newWorkingDairy];
    setWorkingDiaries(newWorkingDiaries);    
  }

  const handleAddWorkingPiece = (belongerId,header,context,timePeriod) => {

    const belongerDiary = workingDairies.filter((workingDairy) => workingDairy.includes(belongerId));
    const formattedDate = belongerDiary.date;
    const newWorkingPieces = {
      id: nanoid(),
      header: header,
      context: context,
      timePeriod : timePeriod,
      date: formattedDate,
      workingPieces: []
    }
    var belongerDiaryPieces = belongerDiary.workingPieces;
    belongerDiaryPieces = [...belongerDiaryPieces, newWorkingPieces];
    setWorkingDiaries(belongerDiary);
  }

  const handleDeleteWorkingPiece = (id) => {

    const newWorkingPieces = workingPieces.filter((workingPiece) => id !== workingPiece.id);
    setWorkingPieces(newWorkingPieces);

  }
  const handleDeleteWorkingDiary = (id) => {

    const newWorkinDiary = workingDairies.filter((workingDairy) => id !== workingDairy.id);
    setWorkingDiaries(newWorkinDiary);

  }


  // const chooseDate = () =>{
  //   var date = new Date($('.date').val());
  //   var formattedDate = date.toLocaleDateString();

  //   setLookingOnDate({
  //     lookingOnDate : formattedDate
  //   })
  // }


    return (
      <div className="container">
      <div className="header">
        <h3 className="text-center mt-4">CHOOSE A DAY AND SEARCH WITH KEYWORDS IN YOUR DAILIES</h3>
      </div>
          <Search 
              chooseDate = {setLookingOnDate}
              handleSearchDaily = {setSearchText}
          />
          <div className="add-diary row mt-4 justify-content-center">
            <button id="new-diary-button" className="btn btn-primary" data-toggle="modal" data-target="#addDaily" toggle="modal" type="button" >Yeni Bir Gün Ekle</button>
          </div>  
          <DiaryList 
              workingDairies = {
                workingDairies.filter((workingDairy) => workingDairy.header.toLowerCase().includes(searchText.toLowerCase() || workingDairy.date === lookingOnDate)
                  )}
              handleAddWorkingDairy = {handleAddWorkingDairy}
              handleAddWorkingPiece = {handleAddWorkingPiece}
              handleDeleteWorkingPiece = {handleDeleteWorkingPiece}
              handleDeleteWorkingDay = {handleDeleteWorkingDiary}

              timePeriodList = {timePeriodList}
          />

          <AddWorkingDaily 
            handleAddWorkingDairy = {handleAddWorkingDairy}
            /> 



      </div>
    )
  }



export default App;
