import React, { Component } from 'react';
import $ from 'jquery';
import Search from './components/Search';
class App extends Component {

  state = {
    lookingOnDate : "",
    workingDiaries : []
  }

  chooseDate = () =>{
    var date = new Date($('.date').val());
    var formattedDate = date.toLocaleDateString();

    this.setState({
      lookingOnDate : formattedDate
    })

  }

  render(){
    return (
      <div className="container">
      <div className="header">
        <h3 className="text-center mt-4">CHOOSE A DAY AND SEARCH WITH KEYWORDS IN YOUR DAILIES</h3>
      </div>
          <Search chooseDate = {this.chooseDate}/>
      </div>
    );
  }

}

export default App;
