import React, { useState } from 'react';
import $ from 'jquery';
import {nanoid} from 'nanoid';
import Search from './components/Search';
import DiaryList from './components/DiaryList';
import AddWorkingDaily from './components/AddWorkingDaily';
import AddWorkingPiece from './components/AddWorkingPiece';






const App = () => {


  const [workingDairies, setWorkingDiaries] = useState([]);
  const [workingPieces, setWorkingPieces] = useState([]);
  const [lookingOnDate, setLookingOnDate] = useState('')
  const [searchText, setSearchText] = useState('');
  const [isVisibleAddWorkingDaily,setisVisibleAddWorkingDaily] = useState(false);
  const [isVisibleAddWorkingPiece,setisVisibleAddWorkingPiece] = useState(false);


  // $.getJSON("localhost:8080/getDiaries",function (data){
  //   this.setState({
  //     workingDairies : data
  //   })
  // });

  const fetchDatas = () => {

    fetch("http://localhost:8080/getDiaries",{ mode: 'cors' })
    .then((response) => { 
      var pulledData = response.json();
      console.log(pulledData);
      setWorkingDiaries(pulledData);
      return pulledData})
    console.log($('.date').val());

  }

  const pageOnLoad = () =>{

    fetchDatas();


  }

  document.getElementsByTagName("body")[0].addEventListener("onload",pageOnLoad);

  const addWorkingDairy = (header,date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    const newWorkingDairy = {
      id: nanoid(),
      header: header,
      date: formattedDate,
      workingPieces: []
    }
    const newWorkingDiaries = [...workingDairies, newWorkingDairy];
    setWorkingDiaries(newWorkingDiaries);    
  }

  const addWorkingPiece = (header,context,date,timePeriod) => {
    const formattedDate = new Date(date).toLocaleDateString();
    const newWorkingDairy = {
      id: nanoid(),
      header: header,
      context: context,
      timePeriod : timePeriod,
      date: formattedDate,
      workingPieces: []
    }
    const newWorkingDiaries = [...workingDairies, newWorkingDairy];
    setWorkingDiaries(newWorkingDiaries);    
  }

  const handleDeleteWorkingPiece = (id) => {

    setWorkingPieces(
      workingPieces.filter(user => id !== user.id)
    )

  }


  // const chooseDate = () =>{
  //   var date = new Date($('.date').val());
  //   var formattedDate = date.toLocaleDateString();

  //   setLookingOnDate({
  //     lookingOnDate : formattedDate
  //   })
  // }


    return (
      <div className="container">
      <div className="header">
        <h3 className="text-center mt-4">CHOOSE A DAY AND SEARCH WITH KEYWORDS IN YOUR DAILIES</h3>
      </div>
          <Search 
              chooseDate = {setLookingOnDate}
              handleSearchDaily = {setSearchText}
          />
          <div className="add-diary row mt-4 justify-content-center">
            <button id="new-diary-button" class="btn btn-primary" data-toggle="modal" data-target="#addDaily" onClick={setisVisibleAddWorkingDaily(true)} type="button" >Yeni Bir GÃ¼n Ekle</button>
          </div>  
          <DiaryList 
              workingDairies = {
                workingDairies.filter((workingDairy) => workingDairy.header.toLowerCase().includes(searchText.toLowerCase())
                  )}
              handleAddWorkingDairy = {addWorkingDairy}
              handleDeleteWorkingPiece = {handleDeleteWorkingPiece}
              isHandleAddWorkingPiece = {setisVisibleAddWorkingPiece}
          />

          <AddWorkingDaily 
            handleAddWorkingDairy = {addWorkingDairy}
            /> 

          <AddWorkingPiece
            handleAddWorkingDairy = {addWorkingPiece}
            />

      </div>
    )
  }



export default App;
