import React, {useState} from 'react'
import AddWorkingPiece from './AddWorkingPiece';
import WorkPiece from './WorkPiece';
import $ from 'jquery';

const WorkDay = ({id,header,date, handleDeleteWorkingDiary, handleAddWorkingPiece, handleDeleteWorkingPiece, timePeriodList, passedWorkingPieces}) => {

        const [isVisible,setIsVisible] = useState(false);
        const [workingPieces,setWorkingPieces] = useState([]);

        const componentOnLoad = () =>{
            setWorkingPieces([passedWorkingPieces]);
        };

        return (

             <div className="row mt-4" onLoad={() => componentOnLoad}>
                    <div id={id} className="card">

                        <div className="card-header d-flex justify-content-between" >
                            <div className="row">
                                <div className="col-md-12">
                                    <h5 id="header" className="d-inline mr-2" style={{cursor:"pointer"}} onClick={() => setIsVisible(!isVisible)}>{header}</h5>
                                    <i onClick={() => handleDeleteWorkingDiary(id,passedWorkingPieces)} className="far fa-trash-alt" style = {{cursor:"pointer"}}></i>
                                </div>
                                <div className="col-md-12 mr-2">
                                    <label id="workingDiaryDate" >{date}</label>
                                    <button id="new-working-piece-button" className="btn btn-primary" data-toggle="modal" data-target="#addWorkingPiece" toggle="modal" type="button" >Yeni Bir İş Parçası Ekle</button>
                                </div>
                            </div>
                       </div>
                        {
                        isVisible ? 
                        <div className="card-body">
                                {workingPieces.map((workingPiece) => (
                                    <WorkPiece
                                        key = {workingPiece.id}
                                        id = {workingPiece.id}
                                        header ={workingPiece.header}
                                        context = {workingPiece.context}
                                        date = {workingPiece.date}
                                        timePeriod = {workingPiece.timePeriod}
                                        handleDeleteWorkingPiece = {handleDeleteWorkingPiece}
                                        handleAddWorkingPiece = {handleAddWorkingPiece}
                                    />
                                ))} 

                        </div> : null
                        }
                        <AddWorkingPiece
                            handleAddWorkingPiece = {handleAddWorkingPiece}
                            workingPieces = {workingPieces}
                            timePeriodList = {timePeriodList}
                            belongerId = {id}
                            inheritedDate = {date}
                        />
                    </div>
            </div>

        )
    }
export default WorkDay;
