import React, { useState } from 'react'

const WorkPiece = ({workingPiece,belongerId,handleDeleteWorkingPiece,timePeriodList}) => {

const[isVisible,setIsVisible] = useState(false);    
    const {id,header,context,date,timePeriod} = workingPiece


    return (

        <div className="row ml-4 working-piece">
            <div className="card">    
                <div className="card-header d-flex">
                            <div className="row working-piece-header-row d-flex justify-content-between">
                                <h5 id="header" className="d-inline" style = {{cursor:"pointer"}} onClick={() => setIsVisible(!isVisible)}>{header}</h5>
                                <MdDeleteForever 
                                        onClick = {() => handleDeleteWorkingPiece(id,belongerId) }
                                        className='delete-icon'
                                        size='1.3em'
                                        style={{cursor:"pointer"}}
                                    />
                                {/* <i onClick = {() => handleDeleteWorkingPiece(id,belongerId)} className="far fa-trash-alt" style = {{cursor:"pointer"}}></i> */}
                            </div>
                            <div className="row working-piece-date-row">
                                <p className="working-piece-date">{date}</p>
                            </div>                          
                </div>
                {
                            isVisible ? 
                            <div className="card-body">
                                    <p className="card-text working-piece-time-period">{timePeriodList[timePeriod]}</p>
                                    <p className="card-text working-piece-context">{context}</p>
                            </div> : null
                            }
            </div>
        </div>


        )

}
export default WorkPiece;
