import React, { Component } from 'react'
import WorkPiece from './WorkPiece';

const WorkDay = ({id,header,workPieces,handleDeleteWorkingDay,handleDeleteWorkingPiece}) => {


    const [isVisible, setIsVisible] = useState(false);

        return (

             <div className="row mt-4">
                    <div id={id} className="card">

                        <div className="card-header d-flex justify-content-between">
                            <h5 id="header" className="d-inline" style={{cursor:"pointer"}} onClick={setIsVisible(!isVisible)}>{header}</h5>
                            <i onClick = {this.onDeleteUser} className="far fa-trash-alt" style = {{cursor:"pointer"}}></i>
                        </div>
                        {
                        isVisible ? 
                        <div className="card-body">
                                {workPieces.map((workPiece) => (
                                <WorkPiece
                                    id = {workPiece.id}
                                    header ={workPiece.header}
                                    context = {workPiece.context}
                                    date = {workPiece.date}
                                    timePeriod = {workPiece.timePeriod}
                                    handleDeleteWorkingPiece = {handleDeleteWorkingPiece}
                                />
                                ))} 

                        </div> : null
                        }
                    </div>
            </div>

        )
    }
export default WorkDay;
