import React, {useState} from 'react'
import AddWorkingPiece from './AddWorkingPiece';
import WorkPiece from './WorkPiece';

const WorkDay = ({id,header,date, handleDeleteWorkingDiary, handleAddWorkingPiece, timePeriodList, passedWorkingPieces}) => {

        const [isVisible,setIsVisible] = useState(false);
        const [workingPieces, setWorkingPieces] = useState (passedWorkingPieces);

        const addWorkingPiece = (workingPieces) =>{

            handleAddWorkingPiece(workingPieces)
        }


        return (

             <div className="row mt-4">
                    <div id={id} className="card">

                        <div className="card-header d-flex justify-content-between">
                            <div className="row">
                                <h5 id="header" className="d-inline mr-2" style={{cursor:"pointer"}} onClick={() => setIsVisible(!isVisible)}>{header}</h5>
                                <i onClick={() => handleDeleteWorkingDiary(id)} className="far fa-trash-alt" style = {{cursor:"pointer"}}></i>
                            </div>
                            <div className="row">
                                <label id="workingDiaryDate" >{date}</label>
                                <button id="new-working-piece-button" className="btn btn-primary" data-toggle="modal" data-target="#addWorkingPiece" toggle="modal" type="button" >Yeni Bir İş Parçası Ekle</button>
                            </div>
                       </div>
                        {
                        isVisible ? 
                        <div className="card-body">
                                {passedWorkingPieces.map(passedWorkingPiece => (
                                    <WorkPiece
                                        id = {passedWorkingPieces.id}
                                        header ={passedWorkingPieces.header}
                                        context = {passedWorkingPieces.context}
                                        date = {passedWorkingPieces.date}
                                        timePeriod = {passedWorkingPieces.timePeriod}
                                        handleDeleteWorkingPiece = {handleDeleteWorkingPiece}
                                        handleAddWorkingPiece = {handleAddWorkingPiece}
                                    />
                                ))} 

                        </div> : null
                        }
                        <AddWorkingPiece
                            handleAddWorkingPiece = {handleAddWorkingPiece}
                            workingPieces = {workingPieces}
                            timePeriodList = {timePeriodList}
                            belongerId = {id}
                            inheritedDate = {date}
                        />
                    </div>
            </div>

        )
    }
export default WorkDay;
