  }

  const [workingDairies, setWorkingDiaries] = useState(workingDiaries);

  const [searchText, setSearchText] = useState('');

  $.getJSON("localhost:8080/diaries",function (data){
    this.setState({

      workingDairies : [...workingDairies] + data

