  const handleAddWorkingDiary = (header,date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    const newWorkingDiary = {
      id: nanoid(),
      header: header,
      date: formattedDate,
      workingPieces: []
    }
    const newWorkingDiaries = [...workingDiaries, newWorkingDiary];
    setWorkingDiaries(newWorkingDiaries);    
  }


  const handleDeleteWorkingDiary = (id) => {

    const newWorkinDiary = workingDiaries.filter((workingDiary) => id !== workingDiary.id);
    setWorkingDiaries(newWorkinDiary);

  }

  const chooseDate = (date) => {

    setLookingOnDate(new Date(date).toLocaleDateString());
  }
  const handleSearchDiary = (searchText) => {

