import React, { useState, Component } from 'react'
import AddWorkingPiece from './AddWorkingPiece';
import WorkPiece from './WorkPiece';
import {nanoid} from 'nanoid';

class WorkDay extends Component{


    state = {
        isVisible: false,
        workingPieces: []
    }

    handleAddWorkingPiece = (header,context,timePeriod) => {

        const inheritedDate = date;
        const newWorkingPiece = {
          id: nanoid(),
          header: header,
          context: context,
          timePeriod : timePeriod,
          date: inheritedDate,
        }
        const newWorkingPieces = [...workingPieces, newWorkingPiece];
        this.setState({
            workingPieces: newWorkingPieces
        });
      }

      handleDeleteWorkingPiece = (id) => {

        const newWorkingPieces = workingPieces.filter((workingPiece) => id !== workingPiece.id);
        setWorkingPieces(newWorkingPieces);

      }
      render(){
        const {id,header,date,handleDeleteWorkingDay,timePeriodList} = this.props;
        return (

             <div className="row mt-4">
                    <div id={id} className="card">

                        <div className="card-header d-flex justify-content-between">
                            <div className="row">
                                <h5 id="header" className="d-inline" style={{cursor:"pointer"}} onClick={setIsVisible(!isVisible)}>{header}</h5>
                                <i onClick = {handleDeleteWorkingDay(id)} className="far fa-trash-alt" style = {{cursor:"pointer"}}></i>
                            </div>
                            <div className="row">
                                <label id="workingDiaryDate" >{}</label>
                                <button id="new-working-piece-button" className="btn btn-primary" data-toggle="modal" data-target="#addWorkingPiece" toggle="modal" type="button" >Yeni Bir İş Parçası Ekle</button>
                            </div>
                       </div>
                        {
                        isVisible ? 
                        <div className="card-body">
                                {workingPieces.map(workingPiece => (
                                    <WorkPiece
                                        id = {workingPiece.id}
                                        header ={workingPiece.header}
                                        context = {workingPiece.context}
                                        date = {workingPiece.date}
                                        timePeriod = {workingPiece.timePeriod}
                                        handleDeleteWorkingPiece = {this.handleDeleteWorkingPiece}
                                        handleAddWorkingPiece = {this.handleAddWorkingPiece}
                                    />
                                ))} 

                        </div> : null
                        }
                        <AddWorkingPiece 
                            timePeriodList = {timePeriodList}
                            belongerId = {id}
                        />
                    </div>
            </div>

        )
    }
}
export default WorkDay;
