import React, { useState } from 'react';

const AddWorkingDaily = ({handleAddDaily}) => {

    const [headerText, setHeaderText] = useState('');
    const [date, setDate] = useState('');

    const handleSaveClick = () => {

      if(headerText.trim().length === 0 || date.trim().length !== 0){
        alert("Lütfen Başlık Kısmını Boş Bırakmayın!");
        }
        else if(headerText.trim().length !== 0 || date.trim().length === 0){
            alert("Lütfen Tarih Kısmını Boş Bırakmayın!");
        }
        else if(headerText.trim().length === 0 || date.trim().length === 0)
        {
            alert("Lütfen Tarih ve Başlık Kısımlarını Boş Bırakmayın!");
        }
        else{
            handleAddDaily(headerText,date);
            setDate('')
            setHeaderText('')
        }





    }

    return (
        <div className="modal fade" id="addDaily" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="addDailyTitle">Yeni Bir Gün Ekle</h5>
              <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
                <div className="row ml-2 mb-4">
                  <label className="mr-4" htmlFor="dailyHeader">Günlük Başlığı</label>
                  <input id="dailyHeader" name="dailyHeader" type="text" value={headerText} onChange={(event) => setHeaderText(event.target.value)}/>
                </div>
                <div className="row ml-2">
                  <label className="mr-4" htmlFor="dailyDate">Günlük Tarihi</label>
                  <input id="dailyDate" name="dailyDate" type="date" value={date} onChange={(event) => setDate(new Date(event.target.value).toLocaleDateString())}  />
                </div>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-dismiss="modal" onClick={()=>{setDate('');setHeaderText('');}}>İptal</button>
              <button type="button" className="btn btn-primary" onClick={handleSaveClick}>Ekle</button>
            </div>
          </div>
        </div>
      </div>
    )
}
export default AddWorkingDaily;
