import React, { useState } from 'react';
import $ from 'jquery';

const AddWorkingPiece = ({handleAddWorkingPiece,timePeriodList}) => {

    const [context,setContext] = useState('');
    const [headerText, setHeaderText] = useState('');
    const [date, setDate] = useState('');
    const [timePeriod,setTimePeriod] = useState('');



    const handleSaveClick = () => {

        if(headerText.trim().length == 0 || date.trim().length != 0){
            alert("Lütfen Başlık Kısmını Boş Bırakmayın!");
        }
        else if(headerText.trim().length == 0 || date.trim().length == 0){
            alert("Lütfen Tarih Kısmını Boş Bırakmayın!");
        }
        else if(headerText.trim().length == 0 || date.trim().length == 0)
        {
            alert("Lütfen Tarih ve Başlık Kısımlarını Boş Bırakmayın!");
        }
        else{
            handleAddWorkingPiece(headerText,date);
            setDate('')
            setHeaderText('')
        }

    fillTheSelectList = () =>{
        var select = $('#workingPieceTimePeriod');
        for (index in timePeriodList){
            select.options[select.options.length] = new Option(timePeriodList[index], index);
        }
    }



    }

    return (
        <div class="modal fade" id="addWorkingPiece" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="addWorkingPieceTitle">Yeni Bir İş Parçası Ekle</h5>
              <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">
                <label for="workingPieceHeader">İş Parçası Başlığı</label>
                <input id="workingPieceHeader" name="dailyHeader" type="text" onChange={(event) => setHeaderText(event.target.value)}/>
                <label for="workingPieceContext">Açıklama</label>
                <input id="workingPieceContext" name="workingPieceContext" type="text" onChange={(event) => setContext(event.target.value)}  />
                <label for="workingPieceTimePeriod">Saat Aralığı</label>
                <select name="timePeriod" id="workingPieceTimePeriod" onSelect={(event) => setTimePeriod(event.target.value)}>
                  {fillTheSelectList()}
                </select>

            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-dismiss="modal" onClick={()=>{setDate('');setHeaderText('');}}>İptal</button>
              <button type="button" class="btn btn-primary" onClick={handleSaveClick}>Ekle</button>
            </div>
          </div>
        </div>
      </div>
    )



}
export default AddWorkingPiece;
