import React, { Component } from 'react'

const WorkPiece = ({id,header,context,date,handleDeleteWorkingPiece}) => {

const[isVisible,setIsVisible] = useState(false);    

    return (

        <div id={id} className="card">
            <div className="card-header d-flex justify-content-between">
                        <div className="row working-piece-header-row">
                            <h5 id="header" className="d-inline" style = {{cursor:"pointer"}} onClick={setIsVisible(!isVisible)}>{header}</h5>
                            <i onClick = {this.handleDeleteWorkingPiece(id)} className="far fa-trash-alt" style = {{cursor:"pointer"}}></i>
                        </div>
                        <div className="row working-piece-date-row">
                            <p className="working-piece-date">{date}</p>
                        </div>
            </div>
            {
                        isVisible ? 
                        <div className="card-body">
                                <p className="card-text working-piece-context">{Context}</p>
                        </div> : null
                        }
        </div>

        )

}
export default WorkPiece;
