import React, { useState } from 'react';
import $ from 'jquery';
import {nanoid} from 'nanoid';
import Search from './components/Search';
import DiaryList from './components/DiaryList';





const App = () => {


  const [workingDairies, setWorkingDiaries] = useState([]);
  const [lookingOnDate, setLookingOnDate] = useState('')
  const [searchText, setSearchText] = useState('');

  $.getJSON("localhost:8080/diaries",function (data){
    this.setState({

      workingDairies : data

    })
  });

  const addWorkingDairy = (header,date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    const newWorkingDairy = {
      id: nanoid(),
      header: header,
      date: formattedDate,
      workingPieces: {}
    }
    const newWorkingDiaries = [...workingDairies, newWorkingDairy];
    setWorkingDiaries(this.newWorkingDiaries);    
  }


  // const chooseDate = () =>{
  //   var date = new Date($('.date').val());
  //   var formattedDate = date.toLocaleDateString();

  //   setLookingOnDate({
  //     lookingOnDate : formattedDate
  //   })

  // }


    return (
      <div className="container">
      <div className="header">
        <h3 className="text-center mt-4">CHOOSE A DAY AND SEARCH WITH KEYWORDS IN YOUR DAILIES</h3>
      </div>
          <Search 
              chooseDate = {setLookingOnDate}
              handleSearchDaily = {setSearchText}
          />
          <DiaryList 
              workingDairies = {
                workingDairies.filter(
                  (workingDairy) => workingDairy.header.toLowerCase().includes(searchText.toLowerCase())
                  )}
              handleAddWorkingDairy = {addWorkingDairy}
          />
      </div>
    )
  }



export default App;
