import React, { useState } from 'react';
import $ from 'jquery';
import {nanoid} from 'nanoid';
import Search from './components/Search';
import DiaryList from './components/DiaryList';
import AddWorkingDiary from './components/AddWorkingDiary';






const App = () => {


  const [workingDiaries, setWorkingDiaries] = useState([
    {
      id: "1",
      header: "Örnek Bir Günlük Başlığı",
      date: "19.09.2021",
      workingPieces: [
        {
          id: "1",
          header: "Örnek Bir İş Parçası Başlığı",
          context: "Bu bir örnek iş parçası içeriğidir",
          date: "19.09.2021",
          timeperiod: 10
        }
      ]
    }
  ]);

  const [lookingOnDate, setLookingOnDate] = useState('')
  const [searchText, setSearchText] = useState('');
  const [isWorkingDayVisible, setIsWorkingDayVisible] = useState(false);
  const [timePeriodList] = useState(
    {
      0: "00:00 - 01:00",
      1: "01:00 - 02:00",
      2: "02:00 - 03:00",
      3: "03:00 - 04:00",
      4: "04:00 - 05:00",
      5: "05:00 - 06:00",
      6: "06:00 - 07:00",
      7: "07:00 - 08:00",
      8: "08:00 - 09:00",
      9: "09:00 - 10:00",
      10: "10:00 - 11:00",
      11: "11:00 - 12:00",
      12: "12:00 - 13:00",
      13: "13:00 - 14:00",
      14: "14:00 - 15:00",
      15: "15:00 - 16:00",
      16: "16:00 - 17:00",
      17: "17:00 - 18:00",
      18: "18:00 - 19:00",
      19: "19:00 - 20:00",
      20: "20:00 - 21:00",
      21: "21:00 - 22:00",
      22: "22:00 - 23:00",
      23: "23:00 - 00:00"
    }
  );
  const setWorkingDayVisibility = () => {
    setIsWorkingDayVisible(true);
  } 


  const handleAddWorkingPiece = (belongerId,inheritedDate,header,context,timePeriod,workingPieces) => {

    const belongerWorkingDiary = workingDiaries.filter((workingDiary) => workingDiary.id === belongerId);

    const newWorkingPiece = {
      id: nanoid(),
      header: header,
      context: context,
      timePeriod : timePeriod,
      date: inheritedDate,
    }
    workingPieces = [...workingPieces, newWorkingPiece];
    belongerWorkingDiary.workingPieces = workingPieces;
  }

  const handleDeleteWorkingPiece = (belongerId,workingPieces) => {

    workingPieces = workingPieces.filter((workingPiece) => belongerId !== workingPiece.id);
    const belongerWorkingDiary = workingDiaries.filter((workingDiary) => workingDiary.id === belongerId);
    belongerWorkingDiary.workingPieces = workingPieces;

  }

  // $.getJSON("localhost:8080/getDiaries",function (data){
  //   this.setState({
  //     workingDairies : data
  //   })
  // });

  const fetchDatas = () => {

    fetch("http://localhost:8080/getDiaries",{ mode: 'cors' })
    .then((response) => { 
      var pulledData = response.json();
      console.log(pulledData);
      setWorkingDiaries(pulledData);
      return pulledData})
    console.log($('.date').val());

   }

   const pageOnLoad = () =>{

     fetchDatas();

   }

  // document.getElementsByTagName("body")[0].addEventListener("onload",pageOnLoad);
  const postData = (data) =>{ 
    var settings = {
    "url": "localhost:8080/ saveWorkDay",
    "method": "POST",
    "timeout": 0,
    "headers": {
      "Content-Type": "application/json"
    },
    "data": JSON.stringify(data),
  };

  $.ajax(settings).done(function (response) {
    alert("Kayıt"+response);
  });
  }

  const handleAddWorkingDiary = (header,date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    const newWorkingDiary = {
      id: nanoid(),
      header: header,
      date: formattedDate,
      workingPieces: []
    }
    const newWorkingDiaries = [...workingDiaries, newWorkingDiary];
    setWorkingDiaries(newWorkingDiaries);
    postData(newWorkingDiary); 
  }


  const handleDeleteWorkingDiary = (id) => {

    const newWorkinDiary = workingDiaries.filter((workingDiary) => id !== workingDiary.id);
    setWorkingDiaries(newWorkinDiary);

  }

  const chooseDate = (date) => {

    setLookingOnDate(new Date(date).toLocaleDateString());
  }
  const handleSearchDiary = (searchText) => {

    setSearchText(searchText);

  }
  // const chooseDate = () =>{
  //   var date = new Date($('.date').val());
  //   var formattedDate = date.toLocaleDateString();

  //   setLookingOnDate({
  //     lookingOnDate : formattedDate
  //   })
  // }


    return (
      <div className="container" onLoad={pageOnLoad}>
        <div className="header">
          <h3 id="app-header" className="text-center mt-4">EFFORT MASTER'A HOŞGELDİNİZ</h3>
        </div>
            <Search 
                chooseDate = {chooseDate}
                handleSearchDiary = {handleSearchDiary}
                searchText = {searchText}
                lookingOnDate = {lookingOnDate}
            />
            <div className="add-diary row mt-4 justify-content-center">
              <button id="new-diary-button" className="btn btn-primary" data-toggle="modal" data-target="#addDiary" toggle="modal" type="button" >Yeni Bir Gün Ekle</button>
            </div>  
            <DiaryList

                workingDiaries = {workingDiaries}
                workingPieces = {workingDiaries.workingPieces}
                timePeriodList = {timePeriodList}
                isWorkingDayVisible = {isWorkingDayVisible}

                handleDeleteWorkingDiary = {handleDeleteWorkingDiary}
                handleAddWorkingPiece = {handleAddWorkingPiece}
                handleDeleteWorkingPiece = {handleDeleteWorkingPiece}
                setWorkingDayVisibility = {setWorkingDayVisibility}

            />

            <AddWorkingDiary
              handleAddWorkingDiary = {handleAddWorkingDiary}
              /> 



      </div>
    )
  }



export default App;
