const AddWorkingPiece = ({handleAddWorkingPiece}) => {

    const [context,setContext] = useState('');
    const [headerText, setHeaderText] = useState('');
    const [date, setDate] = useState('');
    const [timePeriod,setTimePeriod] = useState('');

    const handleSaveClick = () => {

        if(headerText.trim().length == 0 || date.trim().length != 0){
            alert("Lütfen Başlık Kısmını Boş Bırakmayın!");
        }
        else if(headerText.trim().length == 0 || date.trim().length == 0){
            alert("Lütfen Tarih Kısmını Boş Bırakmayın!");
        }
        else if(headerText.trim().length == 0 || date.trim().length == 0)
        {
            alert("Lütfen Tarih ve Başlık Kısımlarını Boş Bırakmayın!");
        }
        else{
            handleAddDaily(headerText,date);
            setDate('')
            setHeaderText('')
        }





    }

    return (
        <div class="modal fade" id="addDaily" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="addDailyTitle">Yeni Bir Gün Ekle</h5>
              <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">
                <label for="dailyHeader">Günlük Başlığı</label>
                <input id="dailyHeader" name="dailyHeader" type="text" onChange={(event) => setHeaderText(event.target.value)}/>
                <label for="dailyDate">Günlük Tarihi</label>
                <input id="dailyDate" name="dailyDate" type="datetime-local" onChange={(event) => setDate(new Date(event.target.value).toLocaleDateString)}  />
            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-dismiss="modal" onClick={()=>{setDate('');setHeaderText('');}}>İptal</button>
              <button type="button" class="btn btn-primary" onClick={handleSaveClick}>Ekle</button>
            </div>
          </div>
        </div>
      </div>
    )
}
export default AddWorkingPiece;
